/** 
 * Highlights the specified text if found in source.
 */jQuery.fn.highlight=function(e){return this.each(function(){var t=this,n=t.innerHTML,r="highlight",i=new RegExp("([^<.]*)("+e+")([^<.]*)","gi"),s="$1<span "+(r?'class="'+r+'"':"")+'">$2</span>$3',o=n.replace(i,s);t.innerHTML=o})};jQuery.fn.removeHighlight=function(){return this.find("span.highlight").replaceWith(function(){return $(this).text()}).end()};