(function(e){var t=e("#qunit-fixture");module("jquery.highlight.js");test(" chainable",function(){t.append("<p>Chain me!</p>");ok(e("p",t).highlight("me").addClass("testing"),"can be chained");equal(e("p",t).hasClass("testing"),!0,"class was added correctly in chaining")});test("highlight simple",function(){t.append('<div id="target">I have some simple text in me</div>');var n=e("#target").highlight("simple text");equal(n.has(".highlight").length,1,"highlighting was added");equal(n.find(".highlight").text(),"simple text","wanted text was highlighted")});test("highlight mixed caps",function(){t.append('<div id="target">I Am CoNfuSinglY WritTen.</div>');var n=e("#target").highlight("confusingly");equal(n.has(".highlight").length,1,"highlighting was added");equal(n.find(".highlight").text(),"CoNfuSinglY","higlighted text was not altered")});test("highlight in multiple elements",function(){expect(3);t.append("<p>I am element one</p><p>I am element two</p>");var n=e("p",t).highlight("element").end();equal(e(".highlight",n).length,2,"highlighting was added");n.find(".highlight").each(function(){equal(e(this).text(),"element","wanted text was higlighted")})});module("jquery.search.js");test(" chainable",function(){t.append("<p>Chain me!</p>");ok(e("p",t).search("me").addClass("testing"),"can be chained");equal(e("p",t).hasClass("testing"),!0,"class was added correctly in chaining")});test("highlight one match in two",function(){t.append("<div>I have some simple text in me</div><div>No match in here</div>");var n=e("div",t).search("simple text");equal(n.filter(".match").length,1,"search found a match")})})(jQuery);